import React, { useState, useEffect } from "react";

/*
  Najma Al Khautar ‚Äî App.jsx
  - Full single-file React component (Tailwind classes used)
  - This file is a complete, runnable React component that exports a valid App() function
  - Fixes the previous error (Minified React error #130) by ensuring a valid component is exported
    and by avoiding rendering raw objects as JSX children.

  Instructions:
  - Paste this file into src/App.jsx of your React project
  - Tailwind is optional but recommended for styling; the component will still render without it
  - Replace /public/images and /public/videos placeholders with your real media files
*/

const PRODUCTS = [
  { id: "rhodes", title: "Rhodes Grass (Bales)", desc: "Green, nutritious and long-lasting bales ‚Äî export-ready." },
  { id: "wheat", title: "Wheat", desc: "High-quality wheat for feed and processing." },
  { id: "corn", title: "Corn", desc: "Moisture-tested corn for premium feed." },
  { id: "barley", title: "Barley", desc: "Fresh barley with consistent quality." },
];

const TRUST_LOGOS = [
  "/images/c:\Users\a\OneDrive\Desktop\rhode-grass-1.jpg.jpg",
  "/images/logo-oman.png",
  "/images/logo-qatar.png",
  "/images/logo-saudi.png",
];

function ProductCard({ product, onQuote }) {
  // product is an object but we render only its primitive fields (title/desc)
  return (
    <div className="bg-white p-5 rounded-2xl shadow hover:shadow-2xl transition transform hover:-translate-y-1">
      <h3 className="font-bold text-lg text-green-900">{product.title}</h3>
      <p className="text-sm text-green-700 mt-2">{product.desc}</p>
      <div className="mt-4 flex gap-2">
        <button onClick={() => onQuote(product.title)} className="px-3 py-2 bg-green-700 text-white rounded-lg font-semibold hover:bg-green-800">Get Quote</button>
        <a href={`mailto:ubaidraza1890@gmail.com?subject=Inquiry about ${encodeURIComponent(product.title)}`} className="px-3 py-2 border rounded-lg">Email</a>
      </div>
    </div>
  );
}

function Counter({ end, label }) {
  const [value, setValue] = useState(0);
  useEffect(() => {
    let start = 0;
    if (!Number.isFinite(end) || end <= 0) return;
    const duration = 1400; // ms
    const stepTime = Math.max(10, Math.floor(duration / end));
    const timer = setInterval(() => {
      start += 1;
      setValue(start);
      if (start >= end) clearInterval(timer);
    }, stepTime);
    return () => clearInterval(timer);
  }, [end]);

  return (
    <div className="text-center p-4">
      <div className="text-3xl md:text-4xl font-extrabold text-green-900">{value}{end >= 100 ? "+" : ""}</div>
      <div className="text-sm text-green-700">{label}</div>
    </div>
  );
}

export default function App() {
  // App state
  const [quoteOpen, setQuoteOpen] = useState(false);
  const [quoteProduct, setQuoteProduct] = useState("");
  const [newsletterMsg, setNewsletterMsg] = useState("");

  useEffect(() => {
    // set a helpful title and meta description for SEO (optional)
    document.title = "Najma Al Khautar ‚Äî Rhodes Grass & Premium Animal Feed";
    let meta = document.querySelector('meta[name="description"]');
    if (!meta) {
      meta = document.createElement('meta');
      meta.name = 'description';
      document.head.appendChild(meta);
    }
    meta.content = "Najma Al Khautar supplies premium Rhodes Grass, wheat, corn and tailored feed mixes ‚Äî export-ready and trusted across multiple countries.";
  }, []);

  function openQuote(productTitle) {
    setQuoteProduct(productTitle || "General Inquiry");
    setQuoteOpen(true);
  }
  function closeQuote() {
    setQuoteOpen(false);
    setQuoteProduct("");
  }

  async function subscribeNewsletter(e) {
    e.preventDefault();
    const form = e.target;
    const data = new FormData(form);
    // Basic network submit to Formspree endpoint
    try {
      const res = await fetch("https://formspree.io/f/xdkwebkn", { method: "POST", body: data });
      if (res.ok) setNewsletterMsg("‚úÖ Subscribed ‚Äî thank you!");
      else setNewsletterMsg("‚ùå Subscription failed. Try again later.");
    } catch (err) {
      setNewsletterMsg("‚ùå Network error.");
    }
    setTimeout(() => setNewsletterMsg(""), 4000);
    form.reset();
  }

  return (
    <div className="min-h-screen bg-fixed text-gray-900 p-6" style={{
      backgroundImage: "linear-gradient(180deg, rgba(242,253,245,0.95), rgba(225,249,235,0.9)), url('/images/greenery-texture.jpg')",
      backgroundBlendMode: 'overlay',
      backgroundSize: 'cover',
      backgroundPosition: 'center'
    }}>

      {/* Floating WhatsApp + Call */}
      <div className="fixed right-6 bottom-6 flex flex-col gap-3 z-50">
        <a href="https://wa.me/923180155643" target="_blank" rel="noreferrer" className="w-14 h-14 rounded-full flex items-center justify-center bg-green-600 text-white shadow-xl hover:scale-105 transform transition" aria-label="WhatsApp">üí¨</a>
        <a href="tel:+923180155643" className="w-14 h-14 rounded-full flex items-center justify-center bg-amber-500 text-white shadow-xl hover:scale-105 transform transition" aria-label="Call">üìû</a>
      </div>

      {/* Header */}
      <header className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between gap-4 mb-8">
        <div className="flex items-center gap-4">
          <div className="w-14 h-14 rounded-2xl bg-green-800 flex items-center justify-center text-white font-extrabold shadow-md">NK</div>
          <div>
            <h1 className="text-2xl font-extrabold">Najma Al Khautar</h1>
            <p className="text-sm text-gray-700">Premium Rhodes Grass & Animal Feed ‚Äî Export Ready</p>
          </div>
        </div>
        <nav className="flex items-center gap-4">
          <a href="#products" className="text-sm font-semibold hover:underline">Products</a>
          <a href="#why" className="text-sm font-semibold hover:underline">Why Us</a>
          <a href="#testimonials" className="text-sm font-semibold hover:underline">Testimonials</a>
          <button onClick={() => openQuote('General Inquiry')} className="px-4 py-2 bg-green-700 text-white rounded-lg font-bold shadow hover:bg-green-800">Get Quote</button>
        </nav>
      </header>

      {/* Hero */}
      <section className="max-w-7xl mx-auto grid md:grid-cols-2 gap-8 items-center mb-10">
        <div>
          <h2 className="text-4xl md:text-5xl font-extrabold text-green-900 leading-tight">Fresh. Green. Trusted ‚Äî Najma Al Khautar</h2>
          <p className="mt-4 text-lg text-green-800">Export-quality Rhodes Grass bales, wheat, corn and tailored feed mixes ‚Äî backed with certificates and fast logistics. Everyone chooses us once ‚Äî and never looks back.</p>
          <div className="mt-6 flex gap-4">
            <button onClick={() => openQuote('Rhodes Grass')} className="px-6 py-3 bg-green-700 text-white rounded-lg font-bold shadow hover:bg-green-800">Request Quote</button>
            <a href="#contact" className="px-6 py-3 border border-green-700 rounded-lg font-bold hover:bg-green-100">Contact</a>
          </div>
          <div className="mt-6 flex gap-6 items-center text-sm text-green-900">
            <div>üìû +92 318 0155643</div>
            <div>‚úâÔ∏è ubaidraza1890@gmail.com</div>
          </div>

          {/* Trust logos */}
          <div className="mt-6 flex items-center gap-4">
            {TRUST_LOGOS.map((logo, idx) => (
              <img key={idx} src={logo} alt={`partner-${idx}`} className="h-8 object-contain" />
            ))}
          </div>
        </div>

        <div>
          <div className="rounded-2xl overflow-hidden shadow-lg">
            <video className="w-full h-auto" autoPlay muted loop playsInline>
              <source src="/videos/rhodegrass-1080p.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
          <div className="mt-4 text-sm text-gray-600">Quality checked ¬∑ Export documentation provided ¬∑ Flexible shipping</div>
        </div>
      </section>

      {/* Why Choose Us */}
      <section id="why" className="max-w-7xl mx-auto grid md:grid-cols-2 gap-8 mb-10">
        <div className="space-y-4">
          <h3 className="text-3xl font-extrabold text-green-900">Why Choose Najma Al Khautar</h3>
          <p className="text-green-800">Because every farmer and buyer deserves a partner they can trust. We don‚Äôt just supply feed ‚Äî we deliver confidence. Below are the reasons almost everyone chooses us as their first and final choice.</p>

          <ul className="mt-4 space-y-3">
            <li className="bg-white/90 p-4 rounded-2xl shadow">‚úÖ <strong>100% Natural & Premium Quality</strong> ‚Äî carefully harvested, sorted, and packed to preserve freshness and color.</li>
            <li className="bg-white/90 p-4 rounded-2xl shadow">‚úÖ <strong>Proven Export Track Record</strong> ‚Äî trusted by buyers across the Middle East and Africa with full export documentation.</li>
            <li className="bg-white/90 p-4 rounded-2xl shadow">‚úÖ <strong>Timely & Transparent Delivery</strong> ‚Äî real-time updates, flexible shipping (sea, air, road), and clear quotes.</li>
            <li className="bg-white/90 p-4 rounded-2xl shadow">‚úÖ <strong>Relationship-first Business</strong> ‚Äî we support repeat buyers with priority allocations and custom mixes.</li>
          </ul>

          <blockquote className="mt-4 p-4 bg-emerald-50 rounded-lg border-l-4 border-green-700">‚ÄúEveryone chooses us once ‚Äî and never looks back.‚Äù</blockquote>

          <div className="mt-6">
            <button onClick={() => openQuote('Partnership')} className="px-5 py-3 bg-amber-500 text-white rounded-lg font-bold shadow hover:bg-amber-600">Partner With Us</button>
          </div>
        </div>

